entity.query:
select 'MCL_'+cast(x.main_id as nvarchar(max))+'_'+isnull(x.cfid,'x')+'_'+cast(row_number() over(order by x.type,x.main_id,x.cfid) as nvarchar(max)) as pkid,x.type,'MCL_'+cast(x.main_id as nvarchar(max)) as main_id,x.cfid,x.title,x.subtitle,x.body,x.url,isnull(sequence,1) as sequence,x.main_id+isnull(x.cfid,'x') as group_s,isnull(tb_image,'') as tb_image,N'' as image_url,N'' as popup_type,N'' as synonym1,N'' as synonym2,N'' as category1,N'' as treevalue,cast(1 as bit) as search_flag
 from ( 
select 'mcalc' as type,cast(id as nvarchar(max)) as main_id,'' as cfid,title as title,'' as subtitle,'' as body,url as url,'1' as sequence,'' as tb_image from T_JP_MedicalCalculator
) x

select 'HOK_'+cast(x.main_id as nvarchar(max))+'_'+isnull(x.cfid,'x')+'_'+cast(row_number() over(order by x.type,x.main_id,x.cfid) as nvarchar(max)) as pkid,x.type,'HOK_'+cast(x.main_id as nvarchar(max)) as main_id,x.cfid,x.title,x.subtitle,x.body,N'Shinryou.aspx?File='+x.url as url,cast(isnull(sequence,1) as integer)*0.7 as sequence,x.main_id+isnull(x.cfid,'x') as group_s,isnull(tb_image,'') as tb_image,N'' as image_url,N'' as popup_type,N'' as synonym1,N'' as synonym2,N'' as category1,N'' as treevalue,cast(1 as bit) as search_flag, contents
 from ( 
select 'hoken' as type,cast(id as nvarchar(max)) as main_id,N'hoken_'+isnull(cast(id as nvarchar(max)),'') as cfid,section as title,subsection as subtitle,body as body,isnull(cast(dir_path as nvarchar(max)),'')+'/'+isnull(cast(file_name as nvarchar(max)),'') as url,'1' as sequence,'' as tb_image, contents from T_JP_Shirobon where (body is not null or body<>'') and dir_path != 'shisetsu'
 UNION
select 'hoken' as type,cast(id as nvarchar(max)) as main_id,N'hoken_'+isnull(cast(id as nvarchar(max)),'') as cfid,section+' '+subsection as title,'' as subtitle,body as body,isnull(cast(dir_path as nvarchar(max)),'')+'/'+isnull(cast(file_name as nvarchar(max)),'') as url,'1' as sequence,'' as tb_image, contents from T_JP_Shirobon where (body is not null or body<>'') and dir_path = 'shisetsu'
) x
